package com.example.mungge_groom.ui.notification

import android.util.Log
import androidx.fragment.app.activityViewModels
import androidx.lifecycle.Lifecycle
import androidx.lifecycle.lifecycleScope
import androidx.lifecycle.repeatOnLifecycle
import com.example.mungge_groom.R
import com.example.mungge_groom.data.model.NotificationData
import com.example.mungge_groom.databinding.FragmentNotificationBinding
import com.example.mungge_groom.extention.GlobalApplication
import com.example.mungge_groom.ui.base.BaseFragment
import com.example.mungge_groom.ui.home.HomeViewModel
import com.example.mungge_groom.ui.mypage.MypageViewModel
import dagger.hilt.android.AndroidEntryPoint
import kotlinx.coroutines.flow.collectLatest
import kotlinx.coroutines.launch

@AndroidEntryPoint
class NotificationFragment :
    BaseFragment<FragmentNotificationBinding>(R.layout.fragment_notification) {
    lateinit var notificationAdapter: NotificationAdapter
    val homeViewModel: HomeViewModel by activityViewModels()
    val mypageViewModel: MypageViewModel by activityViewModels()

    override fun setLayout() {
        setAdapter()
        setProfile()
    }

    private fun setAdapter() {
        homeViewModel.postNotifications()

        notificationAdapter = NotificationAdapter()
    }

    private fun setProfile() {
        val ml = mutableListOf(
            NotificationData(
                "data:image/jpeg;base64,",
                title = "전유선",
                description = "전유선 입니다."
            ),
            NotificationData(
                "data:image/jpeg;base64,",
                title = "박지원",
                description = "박지원 입니다."
            ),
            NotificationData(
                "data:image/jpeg;base64,",
                title = "임수미",
                description = "임수미 입니다."
            ),
            NotificationData(
                "data:image/jpeg;base64,",
                title = "조원희",
                description = "조원희 입니다."
            ),
            NotificationData(
                "data:image/jpeg;base64,",
                title = "권수연",
                description = "권수연 입니다."
            ),
        )
        lifecycleScope.launch {
            repeatOnLifecycle(Lifecycle.State.CREATED) {
                homeViewModel.notifications.collectLatest { not ->
                    Log.d("okhttp",not.size.toString())
                    for (i in not){
                       val u = GlobalApplication.instance.users?.find {
                            i.requester.nickname == it.nickname
                        }
                        u!!.profile_picture ?: "data:image/jpeg;base64,"
                        ml.add(
                            NotificationData(
                                u.profile_picture.toString(),
                                u.nickname,
                                i.requester.intro
                            )
                        )
                    }
                    notificationAdapter.submitList(ml)
                    binding.fragmentNotificationRv.adapter = notificationAdapter
                }
            }
        }
    }
}
